mapper.select:
SELECT `screen`.`index` AS `index`, screen.screening_time AS screeningTime, ADDTIME(screen.screening_time, movie.playing_time) AS endTime, movie.playing_time AS playingTime, movie.title AS movieTitle, movie.dimension_type AS dimensionType, theater.name AS theaterName, cinema.number AS cinemaNumber, movie.thumbnail AS thumbnail, movie.age_limit AS ageLimit, screen.screening_date AS screeningDate FROM terabox.screening_info AS screen JOIN terabox.movies AS movie ON screen.movie_index = movie.index JOIN terabox.cinemas AS cinema ON screen.cinema_index = cinema.index JOIN terabox.theaters AS theater ON cinema.theater_index = theater.index WHERE screen.screening_date = #{screeningDate} AND screen.movie_index IN
#{movieIndex}
AND cinema.theater_index IN
#{theaterIndex}
